import CardItem from '../Card/Card'
import { Grid } from '@mui/material'
import { useState, useEffect } from 'react'

const CardList = ({title})=>{

    const [products, setProducts] = useState([])

    const productos = [
        {
            title : 'Adobo con Ajo 50g',
            price : 30,
            image : 'adoboconajo.png',
            description : '',
            stock : 122,
            id : 1
        },
        {
            title : 'Aji Molido 50g',
            price : 35,
            image : 'ajomolido.png',
            description : '',
            stock : 250,
            id : 2
        },
        {
            title : 'Ajo y Perejil 50g',
            price : 34,
            image : 'ajoyperejil.png',
            description : '',
            stock : 250,
            id : 3
        },
        {
            title : 'Cebolla Roja Triturada 25g',
            price : 36,
            image : 'cebolla.png',
            description : '',
            stock : 300,
            id : 4
        },
        {
            title : 'Ciboulette 25g',
            price : 72,
            image : 'ciboulette.png',
            description : '',
            stock : 197,
            id : 5
        },
        {
            title : 'Curry 7 mares 25g',
            price : 46,
            image : 'curry.png',
            description : '',
            stock : 49,
            id : 6
        },
        {
            title : 'Hinojo 50g',
            price : 45,
            image : 'hinojo.png',
            description : '',
            stock : 88,
            id : 7
        },
        {
            title : 'Salvia 25g',
            price : 36,
            image : 'salvia.png',
            description : '',
            stock : 202,
            id : 8
        },
        {
            title : 'Tandoori 25g',
            price : 48,
            image : 'tandoori.png',
            description : '',
            stock : 92,
            id : 9
        },
    ]

    const getProducts = () => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                resolve(productos);
            }, 3000);
        });
    }
    useEffect (()=>{
        getProducts()
        .then((res) => {
            //console.log('resolved', res);
            setProducts(res)
        })
        .catch((err) => {
            //console.log('rejected', err);
        })
        .finally(() => {
            //console.log('finalizado');
        })
    },[])


    return(
        //Especias
        <>
        {console.log("state products: ", products)}
        <Grid container spacing={3}>
            {
                products.map( ({title, price, image, id}, index)=>{
                    return (
                            <Grid item md={4} key={id}>
                                <CardItem title={title} price={price} image={image}/>
                            </Grid>
                    )
                })
            }
        </Grid>
    </>
    )}

    export default CardList
