    const [open,setOpen] = useState(false)
    const handleClose = ()=>{
    setOpen(false)}
    return (
    <Card sx={{ minWidth: 275 }}>
        <CardContent>
        <div className='card-item'>
            <div>
                <img src={`./images/${image}`}/>
            </div>
            <div>
            <p>{title}</p>
            <span>$ {price}</span>                 
            </div>
            <Button variant={'contained'} color='inherit' onClick={()=> setOpen(true)}>Añadir al carrito</Button>
        </div>
        </CardContent>
        <Modal>
            <p>Se agregó el producto al carrito</p>
        </Modal>
    </Card>
    );
}

export default CardItem;
